<template>
  <div class="wrapper">
    <div class="chat-container"></div>
    <div
      v-for="message in messages"
      :key="message.id"
      :class="{ container: true, darker: message.author != author }"
    >
      <p>{{ message.text }}</p>
      <span
        :class="{
          'time-right': message.author !== author,
          'time-left': message.author === author,
        }"
      >
        {{ message.timestamp }} - {{ message.author }}
      </span>
    </div>

    <div class="footer">
      <label for="msg"></label>
      <input
        type="text"
        class="input-field"
        id="usr"
        v-model="text"
        @keyup.enter="sendMessage"
      />
      <img src="@/assets/send1.svg" class="img-resize" @click="sendMessage" />
    </div>
  </div>
</template>

<script>
import { v4 as uuidv4 } from "uuid";
import socket from "@/modules/socket.js";
import { messages } from "@/router/api.js";

export default {
  props: {
    selectedChannel: {
      type: String,
      default: "",
    },
  },
  data() {
    return {
      author: sessionStorage.getItem("aeroUserName"),
      text: "",
      timestamp: new Date().toLocaleTimeString(),
      messages: [],
    };
  },
  mounted() {
    socket.on("connect", () => {
      console.log(`Connected ${socket.id}`);
    });

    socket.on("message", (message) => {
      console.log(message);
      if (message.channel === this.selectedChannel) {
        this.messages.push(message);
      }
    });

    if (this.selectedChannel) {
      this.fetchMessages();
    }
  },
  watch: {
    selectedChannel(newVal) {
      // Clear the messages array when the selected channel changes
      this.messages = [];
      this.fetchMessages();
    },
  },
  methods: {
    async fetchMessages() {
      try {
        let response = await messages.get(this.selectedChannel);
        console.log(response.data);
        this.messages = response.data;
      } catch (error) {
        console.error(error);
      }
    },
    sendMessage() {
      if (this.text) {
        const message = {
          id: uuidv4(),
          channel: this.selectedChannel,
          author: this.author,
          text: this.text,
          timestamp: new Date().toLocaleTimeString(),
        };
        socket.emit("message", message);
      }
      this.messages.push({
        author: this.author,
        text: this.text,
        timestamp: this.timestamp,
      });
      this.text = "";
    },
  },
};
</script>

